const imageProductCard=document.querySelector(".product-card__image");function floatingLabelColorChange(){let e=document.forms.review.elements.input;for(let t of e)t.onblur=function(){t.value?t.nextElementSibling.style.color="white":t.nextElementSibling.style.color=""}}function zoomImage(e){e.addEventListener("mousemove",(function(t){let n=e.offsetWidth,o=e.offsetHeight,i=t.offsetX/n*100,r=t.offsetY/o*100;imageProductCard.style.backgroundPosition=`${i}% ${r}%`})),e.addEventListener("mouseleave",(function(){e.style.backgroundPosition="center"}))}function bindButtonReview(){let e=document.querySelector(".reviews__button"),t=document.querySelector(".reviews__form-wrapper");e.addEventListener("click",(function(){t.hidden=!t.hidden,"Написать Отзыв"==e.innerHTML?e.innerHTML="Закрыть Отзыв":e.innerHTML="Написать Отзыв"}))}zoomImage(imageProductCard),bindButtonReview(),floatingLabelColorChange();